INSERT INTO tb_notice (notice_no, ntc_title, ntc_write_day, ntc_hits, ntc_content, ntc_del_check, ntc_attached_file1, ntc_attached_file2, board_id, mem_id) VALUES (NOTICE_NO_SEQ.NEXTVAL,'자연맘 고객님 배송이 늦어져 정말 죄송합니다.',SYSDATE,70.0,'안녕하세요 자연맘입니다.<br>핑거루트관련 주문 폭주로인하여 핑거루트주문고객 및 일반주문고객님들의<br>배송 및 배송관리가 원활하지 못해<br>고객님께 불편을 끼쳐드리게 된 점 정말 죄송하다는 말씀을 드리고싶습니다.<br>&nbsp;<br>우선, 저희지역쪽 택배물량이 급증하다보니 택배사측(cj대한통운)과도 소통이 지연되어<br>송장번호 입력이 늦어져 발송이 됐음에도 취소를 해드리거나<br>제품이 잘못 발송된 경우가 많을 것이라 예상이됩니다.<br>&nbsp;<br>이에따라 2/29~3/4까지 오배송된 건들의 문의접수를 받고<br>재발송을 해드리려고합니다.<br>&nbsp;<br>자연맘을 믿고 주문해주셨는데 배송이 늦어져 한번,<br>물건이 잘못 발송되어 두번,<br>배송관련 문의 대응부족 세번,<br>여러번 실망하셨을 고객님들께 정말 죄송하다는 말씀을 드립니다.<br>&nbsp;<br>다음주는 전화문의량이 많아 연결이 원활하지 못할것으로 예상됩니다.<br>카카오톡 플러스친구 @자연맘을 등록하여 오배송건들에 대해 문의를 주시면<br>확인 후 최대한 빠른 시일내 제품을 재발송해드리도록 하겠습니다.<br>(@자연맘 검색 시 나오지않으신다면 @자연으로 검색하시면 16번째 @자연맘을 찾으실 수 있으십니다.)<br>&nbsp;<br>이번 핑거루트사태를 계기로 자연맘 구성원들 모두 비상체제로<br>문제점을 개선해나가고 있사오니 조금만 넓은 아량으로 이해해주시면<br>보다 좋은 제품과 서비스로 보답하는 자연맘이 되도록 노력하겠습니다.<br>감사합니다.<br>',0.0,'null','null',1.0,'admin01');
INSERT INTO tb_faq (faq_no, faq_title, faq_content, faq_del_check, board_id, mem_id) VALUES (FAQ_NO_SEQ.NEXTVAL,'결제 창이 안뜨는데 어떻게 해야하나요?','간혹 결제 창이 안뜬다고 말씀하시는 고객님들이 많으십니다.<br>이 경우 팝업 설정을 차단해놓아서 자동으로 KCP결제 창이 뜨지않기때문에 수동으로 결제창을 열어주셔야합니다.<br>&nbsp;<br>주문결제 시도하셨을때 만약 아래와 같은 화면이 보이신다면 결제하기 버튼을 누르시면 KCP결제 페이지를 보실 수 있으십니다.<br><br><img src=http://i.imgur.com/UdtItem.png width=690><br><br>혹시 위에 사진과 같이 결제하기가 뜨지않고 이상한 문구가 뜨는 경우에는 KCP프로그램이 설치되지않아 발생하는 문제이오니, 결제하는 과정에서 KCP 결제에 필요한 자료를 설치 후 진행해주시기 바랍니다.<br>감사합니다.<br>',0.0,3.0,'admin01');
