INSERT INTO tb_faq (faq_no, faq_title, faq_content, faq_del_check, board_id, mem_id) VALUES (FAQ_NO_SEQ.NEXTVAL,'제품은 어떻게 보관해야 하나요?','저희 자연맘 제품은 원재료 외엔 첨가된 것이 없어 보관을 잘못하실 경우<br>변질되거나 벌레가 생길 수 있으니 보관에 주의하시기 바랍니다.<br>직사광선 및 습기를 피해 건냉한 곳에 보관하시고 냉장 또는 냉동보관 하시는 것이 좋습니다.<br>',0.0,3.0,'admin01');
INSERT INTO tb_faq (faq_no, faq_title, faq_content, faq_del_check, board_id, mem_id) VALUES (FAQ_NO_SEQ.NEXTVAL,'정말 국내산이 맞나요?','조금만 살펴보면 알 수 있는 약초 등은 이미지로 비교하시면 쉽게 알 수 있고<br>구분이 어려운 한방차 등을 전문적으로 취급하시는 분이라면 어렵지 않게 구별하실 수 있습니다.<br>그리고, 수많은 경쟁업체들이 주시하고 있으며 소비자인양 구매를 해서 원산지 허위표시를<br>신고하기 때문에 더욱 철저하게 확인을 하고 있습니다.<br>저희 자연맘은 고객님들의 신뢰를 위해 원산지, 생산자 표기를 하여 보내드리고 있습니다.<br>',0.0,3.0,'admin01');
